mapper.select:
		select c.id,c.AccountNO,a.AccountTypeName,a.StateName,c.MobilePhone,c.SubmitTime,c.MerchantCode,
		a.Balance as aBalance,c.Balance as cBalance,c.ServiceFee, c.RefundTime,c.DealResult,c.DealTime,c.RefundResult
		from BAR_Account_Cancel c LEFT JOIN V_Account a ON  a.AccountNO = c.AccountNO

		  		  (   c.AccountNO LIKE	 '${searchName}%' OR c.MobilePhone LIKE	 '${searchName}%')

		  		   and c.DealResult = #{dealresult} 		  		  
		  		   and c.RefundResult = #{refundresult} 

		  		   and datediff(day,c.SubmitTime,getdate()) >= #{isday} 

				 and c.MerchantCode = #{merchantCode} 



                order by ${sort} ${order}


                order by c.SubmitTime desc



			Offset ${offset} Row Fetch Next ${limit} Rows Only

select  count(*)
from BAR_Account_Cancel c LEFT JOIN BAR_Account a ON  a.AccountNO = c.AccountNO

  		  (   c.AccountNO LIKE	 '${searchName}%' OR c.MobilePhone LIKE	 '${searchName}%')

  		   and c.DealResult = #{dealresult} 		  		  
  		   and c.RefundResult = #{refundresult} 

  		    and datediff(day,c.SubmitTime,getdate()) >= #{isday} 

		 and c.MerchantCode = #{merchantCode}

mapper.update:
update BAR_Account_Cancel
Balance = #{cbalance}, ServiceFee = #{servicefee}, DealResult = #{dealresult}, DealTime = #{dealtime}, DealMsg = #{dealmsg}, DealUID = #{dealuid}
where ID = #{id,jdbcType=BIGINT}
