mapper.select:
SELECT a.AccountNO,a.ID,a.MerchantCode,AccountTypeName.DataValue AS AccountTypeName,StateName.DataValue AS StateName, a.MobilePhone,a.CreatedDate,a.Balance,a.LastDebitTime, a.LastDebitAmount,a.PersonPhotoUrl,a.CertPhotoUrl , a.FreeBalance
From (SELECT AccountNO,ID,AccountType,State,MobilePhone,CreatedDate,Balance,LastDebitTime,LastDebitAmount, PersonPhotoUrl,CertPhotoUrl,MerchantCode, FreeBalance FROM BAR_Account) AS a LEFT OUTER JOIN (SELECT DataCode,DataValue FROM SYS_Dictionary WHERE (DictCode='AccountType')) AS AccountTypeName ON a.AccountType = AccountTypeName.DataCode LEFT OUTER JOIN (SELECT DataCode,DataValue FROM SYS_Dictionary AS SYS_Dictionary_1 WHERE (DictCode='AccountState')) AS StateName ON a.State = StateName.DataCode
( a.AccountNO LIKE '${searchName}%' OR a.MobilePhone LIKE '${searchName}%')
and a.MerchantCode = #{merchantCode} and a.State = #{state}
order by ${sort} ${order}
order by CreatedDate desc

select count(*) From (SELECT AccountNO,ID,AccountType,State,MobilePhone,CreatedDate,Balance,LastDebitTime,LastDebitAmount, PersonPhotoUrl,CertPhotoUrl,MerchantCode FROM BAR_Account) AS a LEFT OUTER JOIN (SELECT DataCode,DataValue FROM SYS_Dictionary WHERE (DictCode='AccountType')) AS AccountTypeName ON a.AccountType = AccountTypeName.DataCode LEFT OUTER JOIN (SELECT DataCode,DataValue FROM SYS_Dictionary AS SYS_Dictionary_1 WHERE (DictCode='AccountState')) AS StateName ON a.State = StateName.DataCode
( a.AccountNO LIKE '${searchName}%' OR a.MobilePhone LIKE '${searchName}%')
and a.MerchantCode = #{merchantCode} and a.State = #{state}

mapper.update:
update BAR_Account
MerchantCode = #{merchantcode}, UID = #{uid}, AccountType = #{accounttype}, AccountNO = #{accountno}, MobilePhone = #{mobilephone}, TrueName = #{truename}, Gender = #{gender}, CertNO = #{certno}, CardTypeCode = #{cardtypecode}, SubCardTypeCode = #{subcardtypecode}, State = #{state}, MaxTxnAmount = #{maxtxnamount}, TopupCounter = #{topupcounter}, DebitCounter = #{debitcounter}, LastMakePayTicketTime = #{lastmakepaytickettime}, LastTopupAmount = #{lasttopupamount}, LastTopupTime = #{lasttopuptime}, LastDebitAmount = #{lastdebitamount}, LastDebitTime = #{lastdebittime}, TotalTopupAmount = #{totaltopupamount}, TotalDebitAmount = #{totaldebitamount}, Balance = #{balance}, Score = #{score}, FreeBalance = #{freebalance}, FreeTimes = #{freetimes}, AccountExpireTime = #{accountexpiretime}, CertPhotoUrl = #{certphotourl}, PersonPhotoUrl = #{personphotourl}, CreatedBy = #{createdby}, CreatedDate = #{createddate}, UpdatedBy = #{updatedby}, UpdatedDate = #{updateddate}, Remark = #{remark}, CertMAC = #{certmac}
where AccountNO = #{accountno}
