mapper.select:
		select ID,DictCode,DataCode,DataValue,OrderNo,State,CreatedBy,CreatedDate,UpdatedBy,UpdatedDate,Remark from SYS_Dictionary

		  		   and ID = #{id} 
		  		   and DictCode = #{type} 
		  		   and DataCode = #{datacode} 
		  		   and DataValue = #{datavalue} 
		  		   and OrderNo = #{orderno} 
		  		   and State = #{state} 
		  		   and CreatedBy = #{createdby} 
		  		   and CreatedDate = #{createddate} 
		  		   and UpdatedBy = #{updatedby} 
		  		   and UpdatedDate = #{updateddate} 
		  		   and Remark = #{remark} 



                order by ${sort} ${order}


                order by ID desc



			Offset ${offset} Row Fetch Next ${limit} Rows Only

select count(*) from SYS_Dictionary

  		   and ID = #{id} 
  		   and DictCode = #{dictcode} 
  		   and DataCode = #{datacode} 
  		   and DataValue = #{datavalue} 
  		   and OrderNo = #{orderno} 
  		   and State = #{state} 
  		   and CreatedBy = #{createdby} 
  		   and CreatedDate = #{createddate} 
  		   and UpdatedBy = #{updatedby} 
  		   and UpdatedDate = #{updateddate} 
  		   and Remark = #{remark}

mapper.update:
update SYS_Dictionary
DictCode = #{DictCode}, DataCode = #{DataCode}, DataValue = #{DataValue}, OrderNo = #{OrderNo}, State = #{State}, CreatedBy = #{CreatedBy}, CreatedDate = #{CreatedDate}, UpdatedBy = #{UpdatedBy}, UpdatedDate = #{UpdatedDate}, Remark = #{Remark}
where ID = #{id}
