mapper.select:
select
from BAR_Article
and BAR_Article.ID like '%${auditRemark}%' OR BAR_Article.Title like '%${auditRemark}%' OR BAR_Article.ArticleType like '${type}' and BAR_Article.CreatedDate >= '${sdate} 0:00:00' and BAR_Article.CreatedDate <= '${edate} 23:59:59' AND MerchantCode = #{merchantCode}
order by ${sort} ${order}
order by CreatedDate desc
Offset ${offset} Row Fetch Next ${limit} Rows Only

mapper.update:
update BAR_Article set
ArticleType=#{arttype}
,Title=#{arttitle}
,PhotoPath=#{imgpath}
,Contents=#{content}
,Abstracts=#{artsummary}
,UpdateBy=#{userid}
,UpdateDate=getdate()
where id=#{id}
