mapper.select:
		select 

        ,SYS_User.UserName as AuditUserName
		from BAR_DailySummary_Topup left join SYS_User on BAR_DailySummary_Topup.AuditUID=SYS_User.ID

             and BAR_DailySummary_Topup.TopupAmount = #{topupAmount}
             and BAR_DailySummary_Topup.PayMethodId = #{payMethodId}
             and BAR_DailySummary_Topup.SummaryDateTime = #{summaryDateTime}
             and BAR_DailySummary_Topup.AuditDateTime = #{auditDateTime}
             and BAR_DailySummary_Topup.AuditStatus = #{auditStatus}
             and BAR_DailySummary_Topup.AuditUID = #{auditUID}
             and BAR_DailySummary_Topup.ServiceFee = #{serviceFee}
             and BAR_DailySummary_Topup.RefundNum = #{refundNum}
             and BAR_DailySummary_Topup.TopupNum = #{topupNum}
             and BAR_DailySummary_Topup.RefundAmount = #{refundAmount}
             and BAR_DailySummary_Topup.TxnDate = #{txnDate}
             and BAR_DailySummary_Topup.AuditRemark like '${auditRemark}%'
             and BAR_DailySummary_Topup.ID = #{iD}
             and BAR_DailySummary_Topup.SummaryDateTime >= '${sdate} 0:00:00'
             and BAR_DailySummary_Topup.SummaryDateTime <= '${edate} 23:59:59'
             and BAR_DailySummary_Topup.PayMethodId = #{rechargetype}
             and BAR_DailySummary_Topup.AuditStatus = #{checktype}



                order by ${sort} ${order}


                order by SummaryDateTime desc



			Offset ${offset} Row Fetch Next ${limit} Rows Only

		select count(*) from BAR_DailySummary_Topup

             and BAR_DailySummary_Topup.TopupAmount = #{topupAmount}
             and BAR_DailySummary_Topup.PayMethodId = #{payMethodId}
             and BAR_DailySummary_Topup.SummaryDateTime = #{summaryDateTime}
             and BAR_DailySummary_Topup.AuditDateTime = #{auditDateTime}
             and BAR_DailySummary_Topup.AuditStatus = #{auditStatus}
             and BAR_DailySummary_Topup.AuditUID = #{auditUID}
             and BAR_DailySummary_Topup.ServiceFee = #{serviceFee}
             and BAR_DailySummary_Topup.RefundNum = #{refundNum}
             and BAR_DailySummary_Topup.TopupNum = #{topupNum}
             and BAR_DailySummary_Topup.RefundAmount = #{refundAmount}
             and BAR_DailySummary_Topup.TxnDate = #{txnDate}
             and BAR_DailySummary_Topup.AuditRemark like '${auditRemark}%'
             and BAR_DailySummary_Topup.ID = #{iD}
             and BAR_DailySummary_Topup.SummaryDateTime >= '${sdate} 0:00:00'
             and BAR_DailySummary_Topup.SummaryDateTime <= '${edate} 23:59:59'
             and BAR_DailySummary_Topup.PayMethodId = #{rechargetype}
             and BAR_DailySummary_Topup.AuditStatus = #{checktype}

        declare @returnMsg VARCHAR(20)
		exec Proc_DailySummary_Topup @returnMsg out,#{txndate}
		select @returnMsg
