mapper.select:
		select 

		from BAR_DebitTransaction left join OPT_Line on BAR_DebitTransaction.[LineNO]=OPT_Line.LineCode

             and (BAR_DebitTransaction.TerminalNO like '${searchvalue}%' or BAR_DebitTransaction.AccountNO like '${searchvalue}%' or BAR_DebitTransaction.DriverNO like '${searchvalue}%' or BAR_DebitTransaction.CompanyCode like '${searchvalue}%' or BAR_DebitTransaction.[LineNO] like '${searchvalue}%')
             and BAR_DebitTransaction.TxnDateTime >= '${sdate} 0:00:00'
             and BAR_DebitTransaction.TxnDateTime <= '${edate} 23:59:59'
            AND BAR_DebitTransaction.MerchantCode IN (select MapMerchantCode from BAR_Merchant WHERE merchantcode= #{merchantCode}) 
             AND BAR_DebitTransaction.[LineNO] = #{lineName}
             AND BAR_DebitTransaction.TerminalNO = #{busNO}
             AND BAR_DebitTransaction.DriverNO = #{driverName}
             AND OPT_Line.[DeptID] = #{deptName}
             AND BAR_DebitTransaction.DebitStatus = #{status}



                order by ${sort} ${order}


                order by TxnDateTime desc



			Offset ${offset} Row Fetch Next ${limit} Rows Only

		select count(*) from BAR_DebitTransaction left join OPT_Line on BAR_DebitTransaction.[LineNO]=OPT_Line.LineCode

             and (BAR_DebitTransaction.TerminalNO like '${searchvalue}%' or BAR_DebitTransaction.AccountNO like '${searchvalue}%' or BAR_DebitTransaction.DriverNO like '${searchvalue}%' or BAR_DebitTransaction.CompanyCode like '${searchvalue}%' or BAR_DebitTransaction.[LineNO] like '${searchvalue}%')
             and BAR_DebitTransaction.TxnDateTime >= '${sdate} 0:00:00'
             and BAR_DebitTransaction.TxnDateTime <= '${edate} 23:59:59'
            AND BAR_DebitTransaction.MerchantCode IN (select MapMerchantCode from BAR_Merchant WHERE merchantcode= #{merchantCode}) 
             AND BAR_DebitTransaction.[LineNO] = #{lineName}
             AND BAR_DebitTransaction.TerminalNO = #{busNO}
             AND BAR_DebitTransaction.DriverNO = #{driverName}
             AND OPT_Line.[DeptID] = #{deptName}
             AND BAR_DebitTransaction.DebitStatus = #{status}
