mapper.select:
select err.MerchantCode merchantCode, err.AccountNO accountNO, err.[LineNO] as [lineNo], aa.LineName lineName, aa.DriverNO driverNo, aa.DeptName deptName, aa.BusNO busNo, err.TerminalNO terminalNO, err.Direction direction, err.StationCode stationCode, err.DoorFlag doorFlag, deb.StartStationTime startStationTime, deb.EndStaionTime endStaionTime, deb.SummaryDateTime summaryDateTime FROM BAR_DebitTransaction_DistanceError err LEFT JOIN BAR_DebitTransaction deb ON err.MerchantCode = deb.MerchantCode LEFT JOIN ( SELECT bu.MerchantCode, bu.BusNO, de.DeptName, li.LineCode, li.LineName, dr.DriverNO, dr.DriverName FROM OPT_Bus bu LEFT JOIN OPT_Driver dr ON bu.LineID = dr.LineID LEFT JOIN OPT_Line li ON bu.DeptID = li.DeptID LEFT JOIN OPT_Department de ON bu.DeptID = de.ID ) aa ON aa.MerchantCode = err.MerchantCode
and (err.AccountNO like '${searchvalue}%' or err.[LineNO] like '${searchvalue}%' or err.TerminalNO like '${searchvalue}%' or aa.DriverNO like '${searchvalue}%') and deb.SummaryDateTime >= '${sdate} 0:00:00' and deb.SummaryDateTime <= '${edate} 23:59:59' AND deb.MerchantCode IN (select MapMerchantCode from BAR_Merchant WHERE merchantcode= #{merchantCode}) AND aa.DeptName = #{deptName} AND aa.LineName = #{lineName} AND aa.DriverNO = #{driverNO} AND aa.BusNO = #{busNO}
order by ${sort} ${order}
order by SummaryDateTime desc
Offset ${offset} Row Fetch Next ${limit} Rows Only
