mapper.select:
select g.id AS gid, g.MsgTypeID,m.TrueName,m.MobilePhone,m.Gender,g.MsgContent,g.SubmitDateTime, g.AttachPhotoUrl,g.LastReplyUID, m.NickName, m.id AS mid, r.id As rid, r.ReplyContent, g.LastReplyDateTime,g.IsResolved, a.TrueName AS aTrueName from BAR_GuestMessage g LEFT JOIN SYS_User a ON g.LastReplyUID = a.ID LEFT JOIN BAR_Member m on m.id = g.uid left JOIN BAR_GuestMessageReply r on r.MsgID = g.ID
and m.MobilePhone like CONCAT('%', #{searchName}, '%') OR m.NickName like CONCAT('%', #{searchName}, '%') and IsResolved = #{state} and IsResolved = #{isresolved} and id = #{id}
GROUP BY g.id, g.MsgTypeID,m.TrueName,m.MobilePhone,m.Gender,g.MsgContent,g.SubmitDateTime, g.AttachPhotoUrl,g.LastReplyUID, m.NickName, m.id, r.id, r.ReplyContent,g.LastReplyDateTime,g.IsResolved, a.TrueName
order by ${sort} ${order}
order by g.SubmitDateTime desc
Offset ${offset} Row Fetch Next ${limit} Rows Only

select count(*) from BAR_GuestMessage

  		   and ID = #{id} 
  		   and MsgTypeID = #{msgtypeid} 
  		   and UID = #{uid} 
  		   and MobilePhone = #{mobilephone} 
  		   and TrueName = #{truename} 
  		   and Gender = #{gender} 
  		   and MsgContent = #{msgcontent} 
  		   and AttachPhotoUrl = #{attachphotourl} 
  		   and SubmitDateTime = #{submitdatetime} 
  		   and LastReplyUID = #{lastreplyuid} 
  		   and LastReplyDateTime = #{lastreplydatetime} 
  		   and IsResolved = #{isresolved}

mapper.update:
update BAR_GuestMessage
MsgTypeID = #{msgtypeid}, UID = #{uid}, MobilePhone = #{mobilephone}, TrueName = #{truename}, Gender = #{gender}, MsgContent = #{msgcontent}, AttachPhotoUrl = #{attachphotourl}, SubmitDateTime = #{submitdatetime}, LastReplyUID = #{lastreplyuid}, LastReplyDateTime = #{lastreplydatetime}, IsResolved = #{isresolved},
where ID = #{id}
