mapper.select:
		select b.ID, b.MerchantCode,b.DeptID,b.BusCode,b.BusNO,b.BusModel,b.BusPlateNO,b.RunCardNO,b.LineID,
		b.SubLineID,b.SamID,b.TerminalNum,b.Factory,b.BuyDate,b.State,b.CreatedDate,b.CreatedBy,b.UpdatedDate,b.UpdatedBy,b.Remark, d.DeptName, l.LineName
		from OPT_Bus b LEFT JOIN  OPT_Department d on d.ID = b.DeptID LEFT JOIN OPT_Line l on l.id = b.LineID

			 and b.BusPlateNO like  CONCAT('%', #{searchName}, '%') or b.BusNO like  CONCAT('%', #{searchName}, '%') 
			 and IsResolved = #{state} 
			 and IsResolved = #{isresolved} 
			 and id = #{id} 
			AND b.MerchantCode = #{merchantCode}



                order by ${sort} ${order}


                order by b.createdDate desc



			Offset ${offset} Row Fetch Next ${limit} Rows Only

select count(*) from OPT_Bus

  		   and ID = #{id} 
  		   and MsgTypeID = #{msgtypeid} 
  		   and UID = #{uid} 
  		   and MobilePhone = #{mobilephone} 
  		   and TrueName = #{truename} 
  		   and Gender = #{gender} 
  		   and MsgContent = #{msgcontent} 
  		   and AttachPhotoUrl = #{attachphotourl} 
  		   and SubmitDateTime = #{submitdatetime} 
  		   and LastReplyUID = #{lastreplyuid} 
  		   and LastReplyDateTime = #{lastreplydatetime} 
  		   and IsResolved = #{isresolved} 
		 AND MerchantCode = #{merchantCode}

mapper.update:
update OPT_Bus
MerchantCode = #{merchantCode}, DeptID = #{deptID}, BusCode = #{busCode}, BusNO = #{busNO}, BusModel = #{busModel}, BusPlateNO = #{busPlateNO}, RunCardNO = #{runCardNO}, LineID = #{lineID}, SubLineID = #{subLineID}, SamID = #{samID}, TerminalNum = #{terminalNum}, Factory = #{factory}, BuyDate = #{buyDate}, State = #{state}, CreatedDate = #{createdDate}, CreatedBy = #{createdBy}, UpdatedDate = #{updatedDate}, Remark = #{remark},
where ID = #{iD}
