mapper.select:
		select 	ID,MerchantCode,DeptCode,DeptName,ParentDeptID as parentId,DeptTypeCode,
				Header,CreatedBy,CreatedDate,UpdatedBy,UpdatedDate,Remark 
				from OPT_Department d

			 and m.MobilePhone like  CONCAT('%', #{searchName}, '%')  OR m.NickName like CONCAT('%', #{searchName}, '%') 
			 and IsResolved = #{state} 
			 and IsResolved = #{isresolved} 
			 and id = #{id} 
			 and DeptCode = #{deptCode} 
			AND MerchantCode = #{merchantCode}



                order by ${sort} ${order}


                order by d.ID desc

select count(*) from OPT_Department

  		   and ID = #{id} 
  		   and MsgTypeID = #{msgtypeid} 
  		   and UID = #{uid} 
  		   and MobilePhone = #{mobilephone} 
  		   and TrueName = #{truename} 
  		   and Gender = #{gender} 
  		   and MsgContent = #{msgcontent} 
  		   and AttachPhotoUrl = #{attachphotourl} 
  		   and SubmitDateTime = #{submitdatetime} 
  		   and LastReplyUID = #{lastreplyuid} 
  		   and LastReplyDateTime = #{lastreplydatetime} 
  		   and IsResolved = #{isresolved} 
		 AND MerchantCode = #{merchantCode}


select 	ID,MerchantCode,DeptCode,DeptName,ParentDeptID as parentId,DeptTypeCode,
Header,CreatedBy,CreatedDate,UpdatedBy,UpdatedDate,Remark 
from OPT_Department where  ID = #{value}

mapper.update:
update OPT_Department
MerchantCode = #{merchantCode}, DeptCode = #{deptCode}, DeptName = #{deptName}, ParentDeptID = #{parentId}, DeptTypeCode = #{deptTypeCode}, Header = #{header}, CreatedBy = #{createdBy}, CreatedDate = #{createdDate}, UpdatedBy = #{updatedBy}, UpdatedDate = #{updatedDate}, Remark = #{remark},
where iD = #{iD}
