mapper.select:
		select *  FROM OPT_Driver d LEFT JOIN OPT_Line l on d.LineID = l.ID


			 and d.DriverNO like  CONCAT('%', #{searchName}, '%')   
			 and d.DriverState = #{type} 
			 and IsResolved = #{isresolved} 
			 and id = #{id} 
			AND d.MerchantCode = #{merchantCode}




                order by ${sort} ${order}


                order by d.ID desc



			Offset ${offset} Row Fetch Next ${limit} Rows Only

select count(*) from OPT_Driver

  		   and ID = #{id} 
  		   and MsgTypeID = #{msgtypeid} 
  		   and UID = #{uid} 
  		   and MobilePhone = #{mobilephone} 
  		   and TrueName = #{truename} 
  		   and Gender = #{gender} 
  		   and MsgContent = #{msgcontent} 
  		   and AttachPhotoUrl = #{attachphotourl} 
  		   and SubmitDateTime = #{submitdatetime} 
  		   and LastReplyUID = #{lastreplyuid} 
  		   and LastReplyDateTime = #{lastreplydatetime} 
  		   and IsResolved = #{isresolved} 
		 AND MerchantCode = #{merchantCode}

mapper.update:
update OPT_Driver
MerchantCode = #{merchantCode}, DriverCode = #{driverCode}, DriverNO = #{driverNO}, DriverName = #{driverName}, DriverCardID = #{driverCardID}, IDCardNO = #{iDCardNO}, IDCardAddr = #{iDCardAddr}, LineID = #{lineID}, Phone = #{phone}, WorkCardNO = #{workCardNO}, BeginWorkDate = #{beginWorkDate}, DriverState = #{driverState}, StateChangeDate = #{stateChangeDate}, DriverImg = #{driverImg}, CreatedDate = #{createdDate}, CreatedBy = #{createdBy}, UpdatedBy = #{updatedBy},
where ID = #{iD}
