mapper.select:
		select  * FROM OPT_Line l LEFT JOIN  OPT_Department d on d.ID = l.DeptID

			 and l.LineCode like  CONCAT('%', #{searchName}, '%')  OR l.LineName like CONCAT('%', #{searchName}, '%') 
			 and l.State = #{type} 
			 and IsResolved = #{isresolved} 
			 and id = #{id} 
			and l.MerchantCode = #{merchantCode}




                order by ${sort} ${order}


                order by l.ID desc



			Offset ${offset} Row Fetch Next ${limit} Rows Only

select count(*) from OPT_Line

  		   and ID = #{id} 
  		   and MsgTypeID = #{msgtypeid} 
  		   and UID = #{uid} 
  		   and MobilePhone = #{mobilephone} 
  		   and TrueName = #{truename} 
  		   and Gender = #{gender} 
  		   and MsgContent = #{msgcontent} 
  		   and AttachPhotoUrl = #{attachphotourl} 
  		   and SubmitDateTime = #{submitdatetime} 
  		   and LastReplyUID = #{lastreplyuid} 
  		   and LastReplyDateTime = #{lastreplydatetime} 
  		   and IsResolved = #{isresolved} 
		 AND MerchantCode = #{merchantCode}

mapper.insert:
insert into OPT_Line (
MerchantCode, DeptID, LineCode, LineName, State, TicketPrice, AttachFee, BusNum, MasterLineID, StationNum, Distance, CreatedDate, CreatedBy
) values (
#{merchantCode,jdbcType=VARCHAR}, #{deptID,jdbcType=INTEGER}, #{lineCode,jdbcType=VARCHAR}, #{lineName,jdbcType=VARCHAR}, #{state,jdbcType=VARCHAR}, #{ticketPrice,jdbcType=DECIMAL}, #{attachFee,jdbcType=DECIMAL}, #{busNum,jdbcType=INTEGER}, #{masterLineID,jdbcType=INTEGER}, #{stationNum,jdbcType=INTEGER}, #{distance,jdbcType=INTEGER}, #{createdDate,jdbcType=TIMESTAMP}, #{createdBy,jdbcType=TIMESTAMP}
)

mapper.update:
update OPT_Line
MerchantCode = #{merchantCode}, DeptID = #{deptID}, LineCode = #{lineCode}, LineName = #{lineName}, State = #{state}, TicketPrice = #{ticketPrice}, AttachFee = #{attachFee}, BusNum = #{busNum}, MasterLineID = #{masterLineID}, StationNum = #{stationNum}, Distance = #{distance}, CreatedDate = #{createdDate}, CreatedBy = #{createdBy}, UpdatedDate = #{updatedDate}, UpdatedBy = #{updatedBy},
where ID = #{iD}
