mapper.select:
		select 

		from BAR_TopupTransaction

             and (BAR_TopupTransaction.OrderNO like '${searchvalue}%' or BAR_TopupTransaction.AccountNO like '${searchvalue}%')
             and BAR_TopupTransaction.TxnDateTime >= '${sdate} 0:00:00'
             and BAR_TopupTransaction.TxnDateTime <= '${edate} 23:59:59'
             and BAR_TopupTransaction.PayMethodId=#{topuptype}
             and BAR_TopupTransaction.PayResult=#{topupstatus}



                order by ${sort} ${order}


                order by TxnDateTime desc



			Offset ${offset} Row Fetch Next ${limit} Rows Only

		select count(*) from BAR_TopupTransaction

             and (BAR_TopupTransaction.OrderNO like '${searchvalue}%' or BAR_TopupTransaction.AccountNO like '${searchvalue}%')
             and BAR_TopupTransaction.TxnDateTime >= '${sdate} 0:00:00'
             and BAR_TopupTransaction.TxnDateTime <= '${edate} 23:59:59'
             and BAR_TopupTransaction.PayMethodId=#{topuptype}
             and BAR_TopupTransaction.PayResult=#{topupstatus}
