mapper.select:
		select id,merchantCode,mapMerchantCode,merchantName,shortName,cityCode,contactPhone,contactPerson,settleMethod,settleRate,
		settlePeriod,settlePeriodUnit,settleDay,serviceFee,lastSettleDateTime,enableStatus from BAR_Merchant

		  		  (   merchantName LIKE	 '${keyWord}%' OR cityCode LIKE	 '${keyWord}%' OR merchantCode LIKE '${keyWord}%' ) 
		  		   AND settleMethod = #{settleMethod} 
		   			 AND merchantCode = #{merchantCode} 
		   			 AND mapMerchantCode = #{mapMerchantCode} 
		  		  AND merchantName = #{merchantName} 
		  		  AND shortName = #{shortName} 
		          AND enableStatus = '1'



                order by ${sort} ${order}


                order by merchantCode desc



			Offset ${offset} Row Fetch Next ${limit} Rows Only

select count(*) from BAR_Merchant

	(   merchantName LIKE	 '${keyWord}%' OR cityCode LIKE	 '${keyWord}%' OR merchantCode LIKE '${keyWord}%' ) 
	 AND settleMethod = #{settleMethod} 
	 AND merchantCode = #{merchantCode} 
	 AND merchantCode = #{merchantName} 
	 AND merchantCode = #{shortName} 
	AND enableStatus = '1'

mapper.update:
update BAR_Merchant
merchantCode = #{merchantCode}, mapMerchantCode = #{mapMerchantCode}, merchantName = #{merchantName}, shortName = #{shortName}, cityCode = #{cityCode}, contactPhone = #{contactPhone}, contactPerson = #{contactPerson}, settleMethod = #{settleMethod}, settleRate = #{settleRate}, settlePeriod = #{settlePeriod}, settlePeriodUnit = #{settlePeriodUnit}, settleDay = #{settleDay}, serviceFee = #{serviceFee}, lastSettleDateTime = #{lastSettleDateTime}, enableStatus = #{enableStatus}, createdBy = #{createdBy}, createdDateTime = #{createdDate}, updatedBy = #{updatedBy}, updatedDateTime = #{updatedDateTime}
where id = #{id}
