mapper.select:
		select ID,RoleID,UserName,PassWord,TrueName,MobilePhone,Gender,BirthDate,
		UserTypeID,StateID,LastLoginDate,LastLoginIP,LoginNum,CreatedBy,CreatedDate,
		UpdatedBy,UpdatedDate,Remark,MerchantCode from SYS_User 

		  		   and ID = #{id}
		  		   and RoleID = #{roleID} 
		  		   and UserName = #{userName} 
		  		   and PassWord = #{passWord} 
		  		   and TrueName = #{trueName} 
		  		   and MobilePhone = #{mobilePhone} 
		  		   and Gender = #{gender} 
		  		   and BirthDate = #{birthDate} 
		  		   and UserTypeID = #{userTypeID} 
		  		   and StateID = #{stateID} 
		  		   and LastLoginDate = #{lastLoginDate} 
		  		   and LastLoginIP = #{lastLoginIP} 
		  		   and LoginNum = #{loginNum} 
		  		   and CreatedBy = #{createdBy} 
		  		   and CreatedDate = #{createdDate} 
		  		   and UpdatedBy = #{updatedBy} 
		  		   and UpdatedDate = #{updatedDate} 
		  		   and Remark = #{remark} 
		  		   and MerchantCode = #{merchantCode} 


		  		  (   UserName LIKE	 '${searchName}%' OR TrueName LIKE	 '${searchName}%')




                order by ${sort} ${order}


                order by ID desc



			Offset ${offset} Row Fetch Next ${limit} Rows Only

select ID,RoleID,UserName,PassWord,TrueName,MobilePhone,Gender,BirthDate,
UserTypeID,StateID,LastLoginDate,LastLoginIP,LoginNum,CreatedBy,CreatedDate,
UpdatedBy,UpdatedDate,Remark,MerchantCode from SYS_User 

  		   and ID = #{id}
  		   and RoleID = #{roleID} 
  		   and UserName = #{userName} 
  		   and PassWord = #{passWord} 
  		   and TrueName = #{trueName} 
  		   and MobilePhone = #{mobilePhone} 
  		   and Gender = #{gender} 
  		   and BirthDate = #{birthDate} 
  		   and UserTypeID = #{userTypeID} 
  		   and StateID = #{stateID} 
  		   and LastLoginDate = #{lastLoginDate} 
  		   and LastLoginIP = #{lastLoginIP} 
  		   and LoginNum = #{loginNum} 
  		   and CreatedBy = #{createdBy} 
  		   and CreatedDate = #{createdDate} 
  		   and UpdatedBy = #{updatedBy} 
  		   and UpdatedDate = #{updatedDate} 
  		   and Remark = #{remark} 
  		   and MerchantCode = #{merchantCode} 



		order by ${sort} ${order}


		order by ID desc



	Offset ${offset} Row Fetch Next ${limit} Rows Only

select count(*) from SYS_User

