mapper.update:
update SYS_User
RoleID = #{roleID,jdbcType=INTEGER}, UserName = #{userName,jdbcType=VARCHAR}, PassWord = #{passWord,jdbcType=VARCHAR}, TrueName = #{trueName,jdbcType=VARCHAR}, MobilePhone = #{mobilePhone,jdbcType=VARCHAR}, Gender = #{gender,jdbcType=VARCHAR}, BirthDate = #{birthDate,jdbcType=TIMESTAMP}, UserTypeID = #{userTypeID,jdbcType=INTEGER}, StateID = #{stateID,jdbcType=INTEGER}, LastLoginDate = #{lastLoginDate,jdbcType=TIMESTAMP}, LastLoginIP = #{lastLoginIP,jdbcType=VARCHAR}, LoginNum = #{loginNum,jdbcType=INTEGER}, CreatedBy = #{createdBy,jdbcType=INTEGER}, CreatedDate = #{createdDate,jdbcType=TIMESTAMP}, UpdatedBy = #{updatedBy,jdbcType=INTEGER}, UpdatedDate = #{updatedDate,jdbcType=TIMESTAMP}, Remark = #{remark,jdbcType=VARCHAR}, MerchantCode = #{merchantCode,jdbcType=VARCHAR}
where ID = #{id,jdbcType=INTEGER}
